// Exec executes the query.
func (u *ParamUpsertBulk) Exec(ctx context.Context) error {
	if u.create.err != nil {
		return u.create.err
	}
	for i, b := range u.create.builders {
		if len(b.conflict) != 0 {
			return fmt.Errorf("entv1: OnConflict was set for builder %d. Set it on the ParamCreateBulk instead", i)
		}
	}
	if len(u.create.conflict) == 0 {
		return errors.New("entv1: missing options for ParamCreateBulk.OnConflict")
	}
	return u.create.Exec(ctx)
}

